INSERT INTO `bs_modification` VALUES ('12', '0', 'So Product Feature', 'ocmod-productoption', 'OpencartWorks', '1.0.0', 'http://www.OpencartWorks.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?> \n<modification> \n <name><![CDATA[So Product Feature]]></name> \n <code>ocmod-productoption</code> \n <version><![CDATA[1.0.0]]></version> \n <author><![CDATA[OpencartWorks]]></author> \n <link>http://www.OpencartWorks.com</link> \n \n<!--========Catalog/controller/product=============--> \n<file path=\"admin/language/*/catalog/product.php\"> \n <operation> \n <search><![CDATA[// Entry]]></search> \n <add position=\"after\"><![CDATA[ \n // Backend Product Options \n $_[\'tab_feature\'] = \'SO Feature <span class=\"label label-primary\">News</span>\'; \n $_[\'entry_video_link\'] = \'Video youtube link\'; \n $_[\'entry_custom_tab_title\'] = \'Title custom tab \'; \n $_[\'entry_description_custom_tab\'] = \'Description custom tab \'; \n \n ]]></add> \n </operation> \n</file> \n <file path=\"admin/model/catalog/product.php\"> \n <operation> \n <search><![CDATA[$this->db->query(\"INSERT INTO \" . DB_PREFIX . \"product_description SET product_id = \'\" . (int)$product_id . \"\', language_id = \'\" . (int)$language_id . \"\', name = \'\" . $this->db->escape($value[\'name\']) . \"\', description = \'\" . $this->db->escape($value[\'description\']) . \"\', tag = \'\" . $this->db->escape($value[\'tag\']) . \"\', meta_title = \'\" . $this->db->escape($value[\'meta_title\']) . \"\', meta_description = \'\" . $this->db->escape($value[\'meta_description\']) . \"\', meta_keyword = \'\" . $this->db->escape($value[\'meta_keyword\']) . \"\'\");]]></search> \n <add position=\"replace\"><![CDATA[ \n if ($this->checkProductOptionColumnExist()) { \n $this->db->query(\"INSERT INTO \" . DB_PREFIX . \"product_description SET product_id = \'\" . (int)$product_id . \"\', language_id = \'\" . (int)$language_id . \"\', name = \'\" . $this->db->escape($value[\'name\']) . \"\', description = \'\" . $this->db->escape($value[\'description\']) . \"\', tag = \'\" . $this->db->escape($value[\'tag\']) . \"\', meta_title = \'\" . $this->db->escape($value[\'meta_title\']) . \"\', meta_description = \'\" . $this->db->escape($value[\'meta_description\']) . \"\', meta_keyword = \'\" . $this->db->escape($value[\'meta_keyword\']) . \"\', video = \'\" . $this->db->escape($value[\'video\']) . \"\', tab_title = \'\" . $this->db->escape($value[\'tab_title\']) . \"\', html_product_tab = \'\" . $this->db->escape($value[\'html_product_tab\']) . \"\'\"); \n } \n else { \n $this->db->query(\"INSERT INTO \" . DB_PREFIX . \"product_description SET product_id = \'\" . (int)$product_id . \"\', language_id = \'\" . (int)$language_id . \"\', name = \'\" . $this->db->escape($value[\'name\']) . \"\', description = \'\" . $this->db->escape($value[\'description\']) . \"\', tag = \'\" . $this->db->escape($value[\'tag\']) . \"\', meta_title = \'\" . $this->db->escape($value[\'meta_title\']) . \"\', meta_description = \'\" . $this->db->escape($value[\'meta_description\']) . \"\', meta_keyword = \'\" . $this->db->escape($value[\'meta_keyword\']) . \"\'\"); \n } \n ]]></add> \n </operation> \n <operation> \n <search><![CDATA[public function getTotalProductsByTaxClassId($tax_class_id) {]]></search> \n <add position=\"before\"><![CDATA[ \n public function checkProductOptionColumnExist() { \n $sql = \"SHOW COLUMNS FROM \" .DB_PREFIX. \"product_description LIKE \'video\'\"; \n $query = $this->db->query($sql); \n return count($query->rows)>0 ? true : false; \n } \n ]]></add> \n </operation> \n <operation> \n <search><![CDATA[$query = $this->db->query(\"SELECT * FROM \" . DB_PREFIX . \"product_description WHERE product_id = \'\" . (int)$product_id . \"\'\");]]></search> \n <add position=\"after\" offset=\"6\"><![CDATA[ \n \'video\' => $product_video, \n \'tab_title\' => $product_tabtitle, \n \'html_product_tab\' => $product_tabproduct, \n ]]></add> \n </operation> \n \n <operation> \n <search><![CDATA[$query = $this->db->query(\"SELECT * FROM \" . DB_PREFIX . \"product_description WHERE product_id = \'\" . (int)$product_id . \"\'\");]]></search> \n <add position=\"after\" offset=\"2\"><![CDATA[ \n $product_video = array(); \n $product_tabtitle = array(); \n $product_tabproduct = array(); \n if(isset($result[\'video\']))$product_video = $result[\'video\']; \n if(isset($result[\'tab_title\']))$product_tabtitle = $result[\'tab_title\']; \n if(isset($result[\'html_product_tab\']))$product_tabproduct = $result[\'html_product_tab\']; \n \n ]]></add> \n </operation> \n \n \n</file> \n \n<file path=\"admin/controller/catalog/product.php\"> \n <operation> \n <search><![CDATA[$data[\'products\'][] = array]]></search> \n <add position=\"before\" ><![CDATA[$product_video = array(); \n $product_tabtitle = array(); \n if(isset($result[\'video\'])){$product_video = $result[\'video\'];} \n if(isset($result[\'tab_title\'])){$product_tabtitle = $result[\'tab_title\'];} ]]></add> \n </operation> \n <operation> \n <search><![CDATA[$data[\'products\'][] = array]]></search> \n <add position=\"after\" offset=\"2\"><![CDATA[\'video\' => $product_video, \n \'tab_title\' => $product_tabtitle,]]></add> \n </operation> \n <operation> \n <search><![CDATA[$data[\'layouts\'] = $this->model_design_layout->getLayouts();]]></search> \n <add position=\"after\"><![CDATA[ \n $checkProductOptionColumn = \"SHOW COLUMNS FROM \" .DB_PREFIX. \"product_description LIKE \'video\'\"; \n $querycheckProductOption = $this->db->query($checkProductOptionColumn); \n $data[\'checkProductOption\']= !empty($querycheckProductOption->rows) ? true : false;]]></add> \n </operation> \n</file> \n<file path=\"admin/view/template/catalog/product_list.twig\"> \n <operation> \n <search><![CDATA[<td class=\"text-left\">{{ product.name }}</td>]]></search> \n <add position=\"replace\"><![CDATA[ \n <td class=\"text-left\">{{ product.name }} {% if product.video or product.tab_title %}<span class=\"label label-primary\">SO Feature</span>{% endif %}</td> \n ]]> \n </add> \n </operation> \n</file> \n<file path=\"admin/view/template/catalog/product_form.twig\"> \n <operation> \n <search><![CDATA[<li class=\"active\"><a href=\"#tab-general\" data-toggle=\"tab\">{{ tab_general }}</a></li>]]></search> \n <add position=\"after\"><![CDATA[{% if checkProductOption %}<li><a href=\"#tab-soproduct\" data-toggle=\"tab\">{{ tab_feature }}</a></li>{% endif %}]]> \n </add> \n </operation> \n <operation> \n <search><![CDATA[ <div class=\"tab-pane\" id=\"tab-data\">]]></search> \n <add position=\"before\"><![CDATA[ \n {% if checkProductOption %} \n <div class=\"tab-pane\" id=\"tab-soproduct\"> \n <ul class=\"nav nav-tabs\" id=\"solanguage\"> \n {% for language in languages %} \n <li><a href=\"#solanguage{{ language.language_id }}\" data-toggle=\"tab\"><img src=\"language/{{ language.code }}/{{ language.code }}.png\" title=\"{{ language.name }}\" /> {{ language.name }}</a></li> \n {% endfor %} \n </ul> \n <div class=\"tab-content\"> \n {% for language in languages %} \n <div class=\"tab-pane\" id=\"solanguage{{ language.language_id }}\"> \n <div class=\"form-group \"> \n <label class=\"col-sm-2 control-label\" for=\"input-video{{ language.language_id }}\"> \n <strong style=\"color:red\">NEW! </strong> \n <span data-toggle=\"tooltip\" title=\"\" data-original-title=\"Enter full video thumbnail link on Product Page\"> {{entry_video_link}} </span> \n <div style=\"font-weight:normal;\"> \n (e.g. https://www.youtube.com/watch?v=Wdtw_A5FDGs) \n </div> \n </label> \n <div class=\"col-sm-10\"> \n \n <input type=\"text\" name=\"product_description[{{ language.language_id }}][video]\" value=\"{{ product_description[language.language_id] ? product_description[language.language_id].video }}\" placeholder=\"{{ entry_video_link }}\" id=\"input-video{{ language.language_id }}\" class=\"form-control\" /> \n \n </div> \n </div> \n \n <div class=\"form-group\"> \n <label class=\"col-sm-2 control-label\" > \n <strong style=\"color:red\">NEW! </strong> \n <span data-toggle=\"tooltip\" title=\"\" data-original-title=\"Enter title for custom tab on Product Page\"> {{entry_custom_tab_title}} </span> \n </label> \n \n <div class=\"col-sm-10\"> \n <input type=\"text\" name=\"product_description[{{ language.language_id }}][tab_title]\" value=\"{{ product_description[language.language_id] ? product_description[language.language_id].tab_title }}\" placeholder=\"{{ entry_custom_tab_title }}\" id=\"input-tab-title{{ language.language_id }}\" class=\"form-control\" /> \n \n </div> \n </div> \n \n <div class=\"form-group\"> \n <label class=\"col-sm-2 control-label\" > \n <strong style=\"color:red\">NEW! </strong> \n <span data-toggle=\"tooltip\" title=\"\" data-original-title=\"Enter any html content for custom tab on Product Page\"> \n {{ entry_description_custom_tab }}</span> \n </label> \n \n <div class=\"col-sm-10\"> \n <textarea name=\"product_description[{{ language.language_id }}][html_product_tab]\" placeholder=\"{{ entry_description }}\" id=\"input-html_product_tab{{ language.language_id }}\" data-toggle=\"summernote\" data-lang=\"{{ summernote }}\" class=\"form-control\">{{ product_description[language.language_id] ? product_description[language.language_id].html_product_tab }}</textarea> \n </div> \n </div> \n \n \n </div> \n {% endfor %} \n </div> \n </div> \n <script type=\"text/javascript\"><!-- \n $(\'#tab-soproduct .nav-tabs > li:first\').tab(\'show\'); \n $(\'#tab-soproduct .tab-content > .tab-pane:first\').addClass(\'active\'); \n --> \n </script> \n {% endif %} \n ]]> \n </add> \n </operation> \n</file> \n \n <file path=\"catalog/controller/product/product.php\"> \n <operation> \n <search ><![CDATA[$data[\'options\'] = array();]]></search> \n <add position=\"before\"><![CDATA[ \n $productoption_info = $this->model_extension_soconfig_general->getProduct($product_id); \n $data[\'product_video\'] = array(); \n $data[\'product_tabtitle\'] = array(); \n $data[\'product_tabcontent\'] = array(); \n if(isset($productoption_info[\'video\'])) $data[\'product_video\'] = $productoption_info[\'video\']; \n if(isset($productoption_info[\'tab_title\'])) $data[\'product_tabtitle\'] = $productoption_info[\'tab_title\']; \n if(isset($productoption_info[\'html_product_tab\'])) $data[\'product_tabcontent\'] = html_entity_decode($productoption_info[\'html_product_tab\'], ENT_QUOTES, \'UTF-8\'); \n ]]></add> \n </operation> \n </file> \n</modification> ', '1', '2018-01-03 15:01:14', '2018-10-27 09:51:03');
