INSERT INTO `bs_modification` VALUES ('15', '4', 'So Dashboard', 'ocmod-dashboard', 'OpencartWorks', '1.0.0', 'http://www.OpencartWorks.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?> \n<modification> \n <name><![CDATA[So Dashboard]]></name> \n <code>ocmod-dashboard</code> \n <version><![CDATA[1.0.0]]></version> \n <author><![CDATA[OpencartWorks]]></author> \n <link>http://www.OpencartWorks.com</link> \n \n<!--========catalog/controller/account=============--> \n<file path=\"catalog/controller/account/edit.php\"> \n <operation> \n <search><![CDATA[$this->response->setOutput($this->load->view(\'account/edit\', $data))]]></search> \n <add position=\"before\"><![CDATA[ \n /*[xml] so dashboard*/ \n $this->load->language(\'extension/module/so_dashboard\'); \n $this->load->model(\'account/customer\'); \n $data[\'customer_info\'] = $this->model_account_customer->getCustomer($this->customer->getId()); \n if ($data[\'customer_info\'][\'custom_field\'] && !empty($data[\'customer_info\']) && !empty(json_decode($data[\'customer_info\'][\'custom_field\'], true))) { \n $data[\'field_addimage\'] = json_decode($data[\'customer_info\'][\'custom_field\'], true); \n $data[\'thumbSrc\'] = $data[\'field_addimage\'][\'profileimage\']; \n $data[\'thumbUrl\'] = $this->model_tool_image->resize($data[\'field_addimage\'][\'profileimage\'], 200, 200); \n } else { \n $data[\'thumbUrl\'] = \'image/placeholder.png\'; \n \n } \n /*[endxml]*/ \n ]]></add> \n \n </operation> \n <operation> \n <search><![CDATA[protected function validate]]></search> \n <add position=\"before\"><![CDATA[ \n /*[xml] so dashboard*/ \n public function uploadprofileimage() { \n $this->load->language(\'tool/upload\'); \n $json = array(); \n \n $allowed = array(\"image/jpeg\", \"image/gif\", \"image/png\"); \n if (!empty($this->request->files[\'file\'][\'name\']) && in_array($this->request->files[\'file\'][\'type\'], $allowed)) { \n // Sanitize the filename \n $file = basename(preg_replace(\'/[^a-zA-Z0-9\\.\\-\\s+]/\', \'\', html_entity_decode($this->request->files[\'file\'][\'name\'], ENT_QUOTES, \'UTF-8\'))); \n if (!$json) { \n // Hide the uploaded file name so people can not link to it directly. \n $this->load->model(\'tool/upload\'); \n $this->load->model(\'tool/image\'); \n $this->load->model(\'account/customer\'); \n \n $filename = time() . \'_\' . md5(rand()) . $file; \n $DIR_PROFILE = DIR_IMAGE.\'profileimage/\'; \n if(!file_exists($DIR_PROFILE)) mkdir($DIR_PROFILE, 0777, true); \n $img_profile = \'profileimage/\'; \n \n move_uploaded_file($this->request->files[\'file\'][\'tmp_name\'], $DIR_PROFILE . $filename); \n $thumbUrl = $this->model_tool_image->resize($img_profile . $filename, 100, 100); \n // Custom Fields \n $data[\'custom_fields\'] = array(); \n $this->load->model(\'account/custom_field\'); \n $custom_fields = $this->model_account_custom_field->getCustomFields($this->config->get(\'config_customer_group_id\')); \n \n $json[\'name\'] = $filename; \n $json[\'thumbPath\'] = $img_profile.$filename; \n $json[\'thumbUrl\'] = $thumbUrl; \n $json[\'success\'] = $this->language->get(\'text_upload\'); \n \n } \n \n } else { \n $json[\'error\'] = $this->language->get(\'error_upload\'); \n } \n $this->response->addHeader(\'Content-Type: application/json\'); \n $this->response->setOutput(json_encode($json)); \n } \n /*[endxml]*/ \n ]]></add> \n \n </operation> \n </file> \n \n<file path=\"catalog/controller/account/account.php\"> \n \n <operation> \n <search><![CDATA[$this->response->setOutput($this->load->view(\'account/account\', $data))]]></search> \n <add position=\"before\"><![CDATA[ \n /*[xml] so dashboard*/ \n if (isset($this->request->get[\'page\'])) { \n $page = $this->request->get[\'page\']; \n } else { \n $page = 1; \n } \n $data[\'orders\'] = array(); \n \n $this->load->model(\'account/order\'); \n \n $order_total = $this->model_account_order->getTotalOrders(); \n \n $results = $this->model_account_order->getOrders(($page - 1) * 10, 10); \n \n foreach ($results as $result) { \n $product_total = $this->model_account_order->getTotalOrderProductsByOrderId($result[\'order_id\']); \n $voucher_total = $this->model_account_order->getTotalOrderVouchersByOrderId($result[\'order_id\']); \n \n $data[\'orders\'][] = array( \n \'order_id\' => $result[\'order_id\'], \n \'name\' => $result[\'firstname\'] . \' \' . $result[\'lastname\'], \n \'status\' => $result[\'status\'], \n \'date_added\' => date($this->language->get(\'date_format_short\'), strtotime($result[\'date_added\'])), \n \'products\' => ($product_total + $voucher_total), \n \'total\' => $this->currency->format($result[\'total\'], $result[\'currency_code\'], $result[\'currency_value\']), \n \'view\' => $this->url->link(\'account/order/info\', \'order_id=\' . $result[\'order_id\'], true), \n ); \n } \n \n $pagination = new Pagination(); \n $pagination->total = $order_total; \n $pagination->page = $page; \n $pagination->limit = 10; \n $pagination->url = $this->url->link(\'account/order\', \'page={page}\', true); \n \n $data[\'pagination\'] = $pagination->render(); \n \n $data[\'results\'] = sprintf($this->language->get(\'text_pagination\'), ($order_total) ? (($page - 1) * 10) + 1 : 0, ((($page - 1) * 10) > ($order_total - 10)) ? $order_total : ((($page - 1) * 10) + 10), $order_total, ceil($order_total / 10)); \n \n $data[\'continue\'] = $this->url->link(\'account/account\', \'\', true); \n $data[\'totalOrder\'] = !empty($data[\'orders\']) ? count($data[\'orders\']) : 0; \n \n // Wishlist \n $this->load->model(\'account/transaction\'); \n $this->load->model(\'account/wishlist\'); \n $this->load->model(\'account/download\'); \n \n if ($this->customer->isLogged()) { \n \n $data[\'totalwishlist\'] = $this->model_account_wishlist->getTotalWishlist(); \n $data[\'text_wishlist\'] = $this->language->get(\'text_wishlist\'); \n } \n //Download \n $data[\'totalDownload\'] = $this->model_account_download->getTotalDownloads(); \n $data[\'totalTransactions\'] = $this->model_account_transaction->getTotalTransactions(); \n \n $this->load->model(\'setting/module\'); \n $this->load->model(\'design/layout\'); \n $data[\'modules\'] = array(); \n \n $layout_id = 0; \n if (isset($this->request->get[\'route\'])) { \n $route = (string)$this->request->get[\'route\']; \n } else { \n $route = \'common/home\'; \n } \n \n if (!$layout_id) { \n $layout_id = $this->model_design_layout->getLayout($route); \n } \n \n $modules = $this->model_design_layout->getLayoutModules($layout_id, \'column_right\'); \n if(empty($modules)) $modules = $this->model_design_layout->getLayoutModules($layout_id, \'column_left\'); \n \n foreach ($modules as $module) { \n $part = explode(\'.\', $module[\'code\']); \n \n if (isset($part[0]) && $part[0]==\'so_dashboard\' && $this->config->get(\'module_\' . $part[0] . \'_status\')) { \n $module_data = $this->load->controller(\'extension/module/\' . $part[0]); \n \n if ($module_data) { \n $data[\'modules\'][] = $module_data; \n } \n } \n if (isset($part[1]) && $part[0]==\'so_dashboard\') { \n $setting_info = $this->model_setting_module->getModule($part[1]); \n if ($setting_info && $setting_info[\'status\']) { \n $data[\'modules\'] = $setting_info; \n \n } \n } \n } \n \n /*[endxml] so dashboard*/ \n ]]></add> \n \n </operation> \n <operation> \n <search><![CDATA[$this->response->setOutput($this->load->view(\'account/account\', $data))]]></search> \n <add position=\"replace\"><![CDATA[ \n $this->response->setOutput($this->load->view(\'account/accountDashboard.\', $data)) \n ]]></add> \n </operation> \n </file> \n \n \n<file path=\"catalog/view/theme/*/template/account/edit.twig\"> \n <operation> \n <search><![CDATA[{{ footer }}]]></search> \n <add position=\"after\"><![CDATA[ \n {#[xml] so dashboard#} \n <style>.profileimage{margin-top: 17px;}</style> \n <script type=\"text/javascript\"><!-- \n $(\'.profileimage\').on(\'click\', function() { \n var node = this; \n \n $(\'#form-upload\').remove(); \n \n $(\'body\').prepend(\'<form enctype=\"multipart/form-data\" id=\"form-upload\" style=\"display: none;\"><input type=\"file\" name=\"file\" /></form>\'); \n \n $(\'#form-upload input[name=\\\'file\\\']\').trigger(\'click\'); \n \n if (typeof timer != \'undefined\') { \n clearInterval(timer); \n } \n \n timer = setInterval(function() { \n if ($(\'#form-upload input[name=\\\'file\\\']\').val() != \'\') { \n clearInterval(timer); \n \n $.ajax({ \n url: \'index.php?route=account/edit/uploadprofileimage\', \n type: \'post\', \n dataType: \'json\', \n data: new FormData($(\'#form-upload\')[0]), \n cache: false, \n contentType: false, \n processData: false, \n beforeSend: function() { \n $(node).button(\'loading\'); \n }, \n complete: function() { \n $(node).button(\'reset\'); \n }, \n success: function(json) { \n $(\'.text-danger\').remove(); \n \n if (json[\'error\']) { \n $(\'#image\').after(\'<div class=\"text-danger\">\' + json[\'error\'] + \'</div>\'); \n } \n \n if (json[\'success\']) { \n alert(json[\'success\']); \n $(\'#image\').attr(\'value\', json[\'thumbPath\']); \n $(\'#imagesrc\').attr(\'src\', json[\'thumbUrl\'] ); \n } \n }, \n error: function(xhr, ajaxOptions, thrownError) { \n alert(thrownError + \"\\r\\n\" + xhr.statusText + \"\\r\\n\" + xhr.responseText); \n } \n }); \n } \n }, 500); \n }); \n //--></script> \n /*[endxml] so dashboard*/ \n ]]></add> \n </operation> \n <operation> \n <search><![CDATA[{% for custom_field in custom_fields %}]]></search> \n <add position=\"before\"><![CDATA[ \n {#[xml] so dashboard#} \n <div class=\"form-group custom-field\" data-sort=\"6\"> \n <label class=\"col-sm-2 control-label\">{{entry_profileImage}}</label> \n <div class=\"col-sm-2 profileimg\"> \n <a class=\"profileimage btn btn-default\"><i class=\"fa fa-upload\" aria-hidden=\"true\"></i> {{entry_upload}}</a> \n <input type=\"hidden\" name=\"custom_field[account][profileimage]\" id=\"image\" value=\"{{thumbSrc}}\"> \n </div> \n <div class=\"col-sm-2\"> \n <img src=\"{{thumbUrl}}\" id=\"imagesrc\" class=\"img-thumbnail\"> \n </div> \n </div> \n {#[endxml] so dashboard#} \n ]]></add> \n </operation> \n </file> \n</modification> ', '1', '2018-10-27 09:52:04', '0000-00-00 00:00:00');
INSERT INTO `bs_modification` VALUES ('16', '47', 'So Google Amp (Accelerated Mobile Pages)', 'ocmod-googleamp', 'OpencartWorks', '1.0.0', 'http://www.opencartWorks.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?> \n<modification> \n <name><![CDATA[So Google Amp (Accelerated Mobile Pages)]]></name> \n <code>ocmod-googleamp</code> \n <version><![CDATA[1.0.0]]></version> \n <author><![CDATA[OpencartWorks]]></author> \n <link>http://www.opencartWorks.com</link> \n \n<!--========Catalog/language/product=============--> \n<file path=\"catalog/language/*/product/product.php\"> \n <operation> \n <search ><![CDATA[// Entry]]></search> \n <add position=\"after\"><![CDATA[ \n $_[\'text_comment\'] = \' Comment\'; \n]]></add> \n </operation> \n \n</file> \n<!--========Catalog/controller/product=============--> \n<file path=\"catalog/controller/product/product.php\"> \n <operation> \n \n <search ><![CDATA[$this->document->addLink($this->url->link(\'product/product\', \'product_id=\' . $this->request->get[\'product_id\']), \'canonical\');]]></search> \n <add position=\"after\"><![CDATA[ \n if($this->config->get(\'so_google_amp_status\')){ \n $this->document->addLink($this->url->link(\'extension/amp/product\', \'product_id=\' . $this->request->get[\'product_id\']), \'amphtml\'); \n if (isset($this->request->get[\'addcart\'])) { \n $this->cart->add($this->request->get[\'product_id\']); \n $this->response->redirect($this->url->link(\'checkout/cart\')); \n } \n } \n]]></add> \n </operation> \n</file> \n \n<file path=\"catalog/controller/product/category.php\"> \n <operation> \n <search ><![CDATA[$data[\'pagination\'] = $pagination->render();]]></search> \n <add position=\"after\"><![CDATA[ \n if($this->config->get(\'so_google_amp_status\')){ \n $this->document->addLink($this->url->link(\'extension/amp/category\', \'path=\' . $category_info[\'category_id\']), \'amphtml\'); \n if (isset($this->request->get[\'addcart\'])) { \n $this->cart->add($this->request->get[\'product_id\']); \n $this->response->redirect($this->url->link(\'checkout/cart\')); \n } \n } \n]]></add> \n </operation> \n</file> \n \n<file path=\"catalog/controller/startup/seo_url.php\"> \n <operation> \n <search><![CDATA[if ($url[0] == \'product_id\') {]]></search> \n <add position=\"before\"><![CDATA[ \n if ($url[0] == \'product_id\' && strpos($this->request->get[\'_route_\'], \"amp/\") !== FALSE) { \n $this->request->get[\'aproduct_id\'] = $url[1]; \n } \n if ($url[0] == \'category_id\' && strpos($this->request->get[\'_route_\'], \"amp/\") !== FALSE) { \n if (!isset($this->request->get[\'apath\'])) { \n $this->request->get[\'apath\'] = $url[1]; \n } else { \n $this->request->get[\'apath\'] .= \'_\' . $url[1]; \n } \n } \n ]]></add> \n </operation> \n <operation> \n <search><![CDATA[foreach ($parts as $part) {]]></search> \n <add position=\"after\"><![CDATA[if ( $part == \'amp\') { \n continue; \n } else \n ]]></add> \n </operation> \n <operation error=\"log\"> \n <search><![CDATA[ \n if (!isset($this->request->get[\'route\'])) { \n ]]></search> \n <add position=\"after\"><![CDATA[ \n if (isset($this->request->get[\'aproduct_id\']) ) { \n $this->request->get[\'route\'] = \'extension/amp/product\'; \n } elseif (isset($this->request->get[\'apath\']) ) { \n $this->request->get[\'route\'] = \'extension/amp/category\'; \n \n ]]></add> \n </operation> \n <operation error=\"log\"> \n <search><![CDATA[ \n if (isset($this->request->get[\'product_id\'])) { \n ]]></search> \n <add position=\"replace\"><![CDATA[ \n } elseif (isset($this->request->get[\'product_id\']) ) { \n ]]></add> \n </operation> \n <operation error=\"log\"> \n <search><![CDATA[ \n ($data[\'route\'] == \'information/information\' && $key == \'information_id\') \n ]]></search> \n <add position=\"replace\"><![CDATA[ \n ($data[\'route\'] == \'information/information\' && $key == \'information_id\') || ($data[\'route\'] == \'extension/amp/product\') \n ]]></add> \n </operation> \n <operation> \n <search><![CDATA[ \n $url .= \